package javabook.ch03;

public class RealNumVarExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float fTemp = 3.14F;
		double dTemp1 = 3.14;
		double dTemp2 = 3.14E5;
		
		System.out.printf("fTemp의 값은 %f입니다.\t\n", fTemp);
		System.out.printf("dTemp1의 값은 %f입니다.\t\n", dTemp1);
		System.out.printf("dTemp2의 값은 %f입니다.", dTemp2);
	}

}
